import UIKit
protocol LibraryDisplayLogic: class {
    func displaySomething(viewModel: Library.ViewModel)
}

class LibraryViewController: UIViewController, LibraryDisplayLogic {
    var interactor: LibraryBusinessLogic?
    var router: (NSObjectProtocol & LibraryRoutingLogic & LibraryDataPassing)?
    @IBOutlet weak var containerView: UIView!
    @IBOutlet weak var heightConstraint: NSLayoutConstraint!
    @IBOutlet weak var descLabel: UILabel!
    @IBOutlet weak var tableView: UITableView!
    var savedDataArray = [Lesson]()
    var overspentDsec: String?
    // MARK: Object lifecycle
    override init(nibName nibNameOrNil: String?, bundle nibBundleOrNil: Bundle?) {
        super.init(nibName: nibNameOrNil, bundle: nibBundleOrNil)
        setup()
    }
    required init?(coder aDecoder: NSCoder) {
        super.init(coder: aDecoder)
        setup()
    }
    // MARK: Setup
    private func setup() {
        let viewController = self
        let interactor = LibraryInteractor()
        let presenter = LibraryPresenter()
        let router = LibraryRouter()
        viewController.interactor = interactor
        viewController.router = router
        interactor.presenter = presenter
        presenter.viewController = viewController
        router.viewController = viewController
        router.dataStore = interactor
    }
    // MARK: View lifecycle
    override func viewDidLoad() {
        super.viewDidLoad()
    }
    override func viewWillAppear(_ animated: Bool) {
        super.viewWillAppear(animated)
        fetchData()
    }
    func displaySomething(viewModel: Library.ViewModel) {
    }
}
extension LibraryViewController {
    func manageTablePlaceholder() {
        setPlaceholder(tableView: self.tableView)
    }
    func setPlaceholder(tableView: UITableView) {
        guard self.savedDataArray.count > 0 else {
            tableView.tableFooterView = nil
            let placeholderView = TransactionEmptyView()
            placeholderView.imgView.image = UIImage(named: .library)
            placeholderView.descLabel.text = .libraryEmptyText
            tableView.backgroundView = placeholderView
            containerView.isHidden = true
            return
        }
        containerView.isHidden = false
        tableView.backgroundView = nil
        tableView.dataSource = self
        tableView.delegate = self
    }
}
